    private function createValidUserAcl() {
        $userAcl = new Core_Model_Acl_User();
        $userAcl->user = 1;
        $userAcl->module = 'core';
        $userAcl->controller = 'index';
        $userAcl->action = 'new';
        $userAcl->collection = 3;
        $userAcl->object = 2;
        $userAcl->permission = 1;

        $result = $this->service->create($userAcl);

        $this->assertInstanceOf('Core_Model_Acl_User_Interface', $result);
        return $result;
    }

    private function createTestUserAcls() {
        // $this->createValidUserAcl();
        $acls = array();

        for ($i = 1; $i <= 3; $i++) {

            $base = array(
                'user' => $i,
                'module' => null,
                'controller' => null,
                'action' => null,
                'object' => null,
                'permission' => 1);

            $acls[] = $base; //deny all
            //null values
            //deny all to demo 
            $base['module'] = 'demo';
            $acls[] = $base;

            //$base['permission'] = 1;
            //grant view to all demo items
            $acl = $base;
            $base['module'] = 'demo';
            $acl['action'] = 'view';
            $acls[] = $acl;
            
            $base['controller'] = 'test';

            //core
            $acl = $base;
            $acl['module'] = 'core';

            $acl['action'] = 'new';
            $acls[] = $acl;

            $acl['action'] = 'delete';
            $acls[] = $acl;

            //test
            $acl = $base;
            $acl['module'] = 'test';
            $acl['action'] = 'new';
            $acls[] = $acl;

            $acl['action'] = 'edit';
            $acls[] = $acl;

            $acl['action'] = 'delete';
            $acls[] = $acl;

            //specfic objects
            $acl['action'] = 'view';
            $acl['object'] = 1;
            $acls[] = $acl;

            $acl['object'] = 2;
            $acls[] = $acl;

            $acl['object'] = 3;
            $acl['collection'] = 1;
            $acls[] = $acl;
        }
        foreach ($acls as $acl) {
            $this->service->create($acl);
        }
    }

